import './App.css'
 function Header(){
  function beActive(e){
    let isActive = document.querySelector('.active');
    if(isActive){
      isActive.classList.remove('active');
    }

    e.target.classList.add('active');


  }
  return(
    <>
      <div>
        <ul className='headerUl'>
          <li onClick={() => {beActive(e)}} className='headerLi active' key='top250' id='1'>Top 250 Movies</li>
          <li onClick={() => {beActive(e)}} className='headerLi' key='top100' id='2'>Top 100 Movies</li>
          <li onClick={() => {beActive(e)}} className='headerLi' key='topbycategory' id='3'>Top 100 Movies By Category</li>
        </ul>
      </div>
    </>
  )
}

function ImageArea(){
  return(
    <>
    <div className='imageArea'>
      <h1 className='imageAreaH1'>
        Create Your Dream Movie List with Bera.Dev
      </h1>
      <br></br>
      <br></br>
      <br></br>
      <p className='imageAreaP'>
        Choose movies from IMDb top rated movies and create you a new movie list.  
      </p>      
    </div>
    </>
  )
}


export default function App(){
  return(
    <>
    <Header />
    <ImageArea />

    </>
  )
}
