import MoviesArray from "./data";
import './App.css';
import { useState } from "react";
import 'bootstrap/dist/css/bootstrap.css';



const userList = [
  {
    id: 0,
    title: 'First Movie',
    imdbRating: 7.7,
    genre: 'Action',
    plot: 'This movie is so scary',
    director: 'Bera',
    actors: 'Herry, Merry, Jenny, Penny',
    year: '0010',
    poster: 'https://m.media-amazon.com/images/M/MV5BYWZjMjk3ZTItODQ2ZC00NTY5LWE0ZDYtZTI3MjcwN2Q5NTVkXkEyXkFqcGdeQXVyODk4OTc3MTY@._V1_SX300.jpg'
  }
]




function UserList({userLastList, setUserLastList}){
  function movieDelete(m) {
    const thisList = userLastList.filter((movie) => (movie.id !== m.movie.id));
    setUserLastList(thisList);

  }
  return(
    <>
    <table className="movieTable">
      <tr>
        <th>Movie Name</th>
        <th>IMDb</th>
        <th>Genre</th>
        <th>Plot</th>
        <th>Director</th>
        <th>Actors</th>
        <th>Year</th>
        <th>Poster</th>
        <th>Delete from Your List</th>
      </tr>
      {userLastList.map((movie) => (
        <tr>
          <td key={movie.title}>
            {movie.title}
          </td>
          <td key={movie.imdbRating}>
            {movie.imdbRating}
          </td>
          <td key={movie.genre}>
            {movie.genre}
          </td>
          <td key={movie.plot}>
            {movie.plot}
          </td>
          <td key={movie.director}>
            {movie.director}
          </td>
          <td key={movie.actors}>
            {movie.actors}
          </td>
          <td key={movie.Title + movie.year}>
            {movie.year}
          </td>
          <td  key={movie.poster}>
            <img className="moviePoster" src={movie.poster}></img>
          </td>
          <td>
            <button onClick={() => (
              movieDelete({ movie })
            )}>Delete</button>
          </td>
        </tr>
      ))}
      </table>
    </>
  )
}

function MovieList(){
  const [userLastList, setUserLastList] = useState(userList);
  const [nextId, setNextId] = useState(1);

  const [currentPage, setCurrentPage] = useState(1);
  const recordsPerPage = 5;
  const lastIndex = currentPage * recordsPerPage;
  const firstIndex = lastIndex - recordsPerPage;
  const records = MoviesArray.slice(firstIndex, lastIndex);
  const npage = Math.ceil(MoviesArray.length / recordsPerPage);
  const numbers = [...Array(npage + 1).keys()].slice(1);

  function addToList(e){
    setNextId(nextId + 1);

    setUserLastList([
      ...userLastList,
      {
        id: nextId,
        title: e.movie.Title,
        imdbRating: e.movie.imdbRating,
        genre: e.movie.Genre,
        plot: e.movie.Plot,
        director: e.movie.Director,
        actors: e.movie.Actors,
        year: e.movie.Year,
        poster: e.movie.Poster
      }
    ])
    console.log(userLastList);

  }
  return(
    <>
    <div className='listArea'>
      <div>
      <table className="movieTable">
      <tr>
        <th>Movie Name</th>
        <th>IMDb</th>
        <th>Genre</th>
        <th>Plot</th>
        <th>Director</th>
        <th>Actors</th>
        <th>Year</th>
        <th>Poster</th>
        <th>Add to Your List</th>
      </tr>
      {MoviesArray.map((movie) => (
        <tr key={movie.title}>
          <td>
            {movie.Title}
          </td>
          <td>
            {movie.imdbRating}
          </td>
          <td>
            {movie.Genre}
          </td>
          <td>
            {movie.Plot}
          </td>
          <td>
            {movie.Director}
          </td>
          <td>
            {movie.Actors}
          </td>
          <td>
            {movie.Year}
          </td>
          <td>
            <img className="moviePoster" src={movie.Poster}></img>
          </td>
          <td>
            <button onClick={() => {
              addToList({movie})
            }}>Add</button>
          </td>
        </tr>
      ))}
      </table>
      <nav>
        <ul className="pagination">
            <li className="page-item">
              <a href='#' 
              className="page-link"
              onClick={prePage}
              >Prev</a>
            </li>
            {
              numbers.map((n, i) => (
                <li className={`page-item ${currentPage === n ? 'active' : ''}`} key={i}>
                  <a href="#" className="page-item"
                  onClick={changeCurrentPage}>{n}</a>
                </li>
              ))
            }
        </ul>
      </nav>

      </div>
      <div>
        <UserList userLastList={userLastList} setUserLastList={setUserLastList}/>
      </div>
    </div>
    </>
  )
}
export default function App(){
  return(
    <>
    <div className="listing">
    <MovieList />


    </div>

    </>
  )
}
