import MoviesArray from "./data";
import './App.css';
import { useState } from "react";


const userList = [
  {
    id: 0,
    title: 'First Movie',
    imdbRating: 7.7,
    genre: 'Action',
    plot: 'This movie is so scary',
    director: 'Bera',
    actors: 'Herry, Merry, Jenny, Penny',
    year: '0010',
    poster: 'https://m.media-amazon.com/images/M/MV5BYWZjMjk3ZTItODQ2ZC00NTY5LWE0ZDYtZTI3MjcwN2Q5NTVkXkEyXkFqcGdeQXVyODk4OTc3MTY@._V1_SX300.jpg'
  }
]

function UserList({userLastList}){
  return(
    <>
    <table className="movieTable">
      <tr>
        <th>Movie Name</th>
        <th>IMDb</th>
        <th>Genre</th>
        <th>Plot</th>
        <th>Director</th>
        <th>Actors</th>
        <th>Year</th>
        <th>Poster</th>
        <th>Delete from Your List</th>
      </tr>
      {userLastList.map((movie) => (
        <tr>
          <td key={movie.title}>
            {movie.title}
          </td>
          <td key={movie.imdbRating}>
            {movie.imdbRating}
          </td>
          <td key={movie.genre}>
            {movie.genre}
          </td>
          <td key={movie.plot}>
            {movie.plot}
          </td>
          <td key={movie.director}>
            {movie.director}
          </td>
          <td key={movie.actors}>
            {movie.actors}
          </td>
          <td key={movie.Title + movie.year}>
            {movie.year}
          </td>
          <td  key={movie.poster}>
            <img className="moviePoster" src={movie.poster}></img>
          </td>
          <td>
            <button>Delete</button>
          </td>
        </tr>
      ))}
      </table>
    </>
  )
}

function MovieList(){
  const [userLastList, setUserLastList] = useState(userList);
  const [nextId, setNextId] = useState(0);

  function addToList(e){
    setUserLastList([
      ...userLastList,
      {
        id: setNextId(nextId => nextId + 1),
        title: e.movie.Title,
        imdbRating: e.movie.imdbRating,
        genre: e.movie.Genre,
        plot: e.movie.Plot,
        director: e.movie.Director,
        actors: e.movie.Actors,
        year: e.movie.Year,
        poster: e.movie.Poster
      }
    ])
  }
  return(
    <>
    <div className='listArea'>
      <table className="movieTable">
      <tr>
        <th>Movie Name</th>
        <th>IMDb</th>
        <th>Genre</th>
        <th>Plot</th>
        <th>Director</th>
        <th>Actors</th>
        <th>Year</th>
        <th>Poster</th>
        <th>Add to Your List</th>
      </tr>
      {MoviesArray.map((movie) => (
        <tr>
          <td key={movie.Title}>
            {movie.Title}
          </td>
          <td key={movie.imdbRating}>
            {movie.imdbRating}
          </td>
          <td key={movie.Genre}>
            {movie.Genre}
          </td>
          <td key={movie.Plot}>
            {movie.Plot}
          </td>
          <td key={movie.Director}>
            {movie.Director}
          </td>
          <td key={movie.Actors}>
            {movie.Actors}
          </td>
          <td key={movie.Title + movie.Year}>
            {movie.Year}
          </td>
          <td  key={movie.Poster}>
            <img className="moviePoster" src={movie.Poster}></img>
          </td>
          <td>
            <button onClick={() => {
              addToList({movie})
            }}>Add</button>
          </td>
        </tr>
      ))}
      </table>
      <div>
        <UserList userLastList={userLastList}/>
      </div>
    </div>
    </>
  )
}
export default function App(){
  return(
    <>
    <div className="listing">
    <MovieList />


    </div>

    </>
  )
}
