import './App.css';

const List = [
  {
    id:0,
    name: 'MovieName 1',
    imdB: 9.3,
    description: 'lala'
  },
  {
    id:1,
    name: 'MovieName 1',
    imdB: 9.3,
    description: 'lala'
  },
  {
    id:2,
    name: 'MovieName 1',
    imdB: 9.3,
    description: 'lala'
  }
]

function Header() {
  function beActive(event) {
    let isActive = document.querySelector('.active');
    if (isActive) {
      isActive.classList.remove('active');
    }

    event.target.classList.add('active');
  }

  return (
    <>
      <div>
        <ul className='headerUl'>
          <li onClick={beActive} className='headerLi active' key='top250' >
            Top 250 Movies
          </li>
          <li onClick={beActive} className='headerLi' key='top100' >
            Top 100 Movies
          </li>
          <li onClick={beActive} className='headerLi' key='topbycategory' >
            Top 100 Movies By Category
          </li>
        </ul>
      </div>
    </>
  );
}

function ImageArea() {
  return (
    <>
      <div className='imageArea'>
        <h1 className='imageAreaH1'>Create Your Dream Movie List with Bera.Dev</h1>
        <br></br>
        <br></br>
        <br></br>
        <p className='imageAreaP'>Choose movies from IMDb top-rated movies and create your new movie list.</p>
      </div>
    </>
  );
}

export default function App() {
  return (
    <>

      <ImageArea />
      <Header />
    </>
  );
}
