import { useState, useEffect } from 'react';

const Fetch = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const fetchMovies = async () => {
      const url = 'https://imdb-top-100-movies.p.rapidapi.com/top32';
      const options = {
        method: 'GET',
        headers: {
          'X-RapidAPI-Key': '189773483emshdf5c2b3eaeea748p1e6fe0jsnaf1dd42e45d5',
          'X-RapidAPI-Host': 'imdb-top-100-movies.p.rapidapi.com'
        }
      };

      try {
        const response = await fetch(url, options);
        const result = await response.json();
        console.log(result);
        setMovies(result);
        forceUpdate(); // Force a re-render
      } catch (error) {
        console.error(error);
      }
    };

    fetchMovies();
  }, []);

  const forceUpdate = () => {
    setMovies([...movies]); // Create a new array to trigger a re-render
  };

  console.log(movies);

  return (
    <div>
      {movies.length > 0 ? (
        movies.map((movie) => (
          <div key={movie.id}>
            <h2>{movie.title}</h2>
            <p>Year: {movie.year}</p>
            {/* Add more movie details as needed */}
          </div>
        ))
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default Fetch;
