import MoviesArray from "./data";
import './App.css';
import { useState } from "react";
import 'bootstrap/dist/css/bootstrap.css';



const userList = [
  {
    id: 0,
    title: 'First Movie',
    imdbRating: 7.7,
    genre: 'Action',
    plot: 'This movie is so scary',
    director: 'Bera',
    actors: 'Herry, Merry, Jenny, Penny',
    year: '0010',
    poster: 'https://m.media-amazon.com/images/M/MV5BYWZjMjk3ZTItODQ2ZC00NTY5LWE0ZDYtZTI3MjcwN2Q5NTVkXkEyXkFqcGdeQXVyODk4OTc3MTY@._V1_SX300.jpg'
  }
]




function UserList({userLastList, setUserLastList}){
  const [isMovieList, setIsMovieList] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const recordsPerPage = 5;
  const lastIndex = currentPage * recordsPerPage;
  const firstIndex = lastIndex - recordsPerPage;
  const records = userLastList.slice(firstIndex, lastIndex);
  const npage = Math.ceil(userLastList.length / recordsPerPage);
  const numbers = [...Array(npage + 1).keys()].slice(1);

  function handleList(){
    setIsMovieList(!isMovieList);
  }

  function changeCurrentPage(id) {
    setCurrentPage(id);
  }
  function prePage(){
