import { useState } from "react";

const todoArray = ['temizlik', 'yemek'];


function Button({valueNew}) {


  function consoleNew(){
  console.log(valueNew);
  }  
  return(
    <>
    <button onClick={consoleNew}>Add</button>
    </>
  )
}



function Input(){
  const [newValue, setNewValue] = useState('');

  return(
    <>
    <input
    className="inputClass"
    onChange={event => setNewValue(event.target.value)}
    value={newValue}    
    />
    <Button valueNew={newValue} />
    </>
  )
}



export default function App(){
  return(
    <>
    <Input />
    <ol>
      <li key={todoArray}>
        {todoArray}
      </li>
    </ol>


    </>
  )
}
