import { useRef } from "react";
import { useReactToPrint } from 'react-to-print';


export default function Pdf({userLastList}){
    const componentPDF = useRef();
    const isMovie = true;

    const generatePDF=useReactToPrint({
        content: () => componentPDF.current,
        documentTitle: "WatchList",
         onAfterPrint: () => alert("List saved in PDF")
    })
      
       

    return (
        <>
        {(!isMovie) ? (<button onClick={generatePDF}>PDF</button>) : (<div ref={componentPDF} style={{'width':'100%'}}>
        <table className="movieTable">
          <tr>
            <th>Movie Name</th>
            <th>IMDb</th>
            <th>Genre</th>
            <th>Plot</th>
            <th>Director</th>
            <th>Actors</th>
            <th>Year</th>
            <th>Poster</th>
            <th>Delete from Your List</th>
          </tr>
          {userLastList.map((movie) => (
            <tr key={movie.Title}>
              <td data-cell="title">
                {movie.title}
              </td>
              <td data-cell="imdb">
                {movie.imdbRating}
              </td>
              <td data-cell="genre">
                {movie.genre}
              </td>
              <td data-cell="plot">
                {movie.plot}
              </td>
              <td data-cell="director">
                {movie.director}
              </td>
              <td data-cell="title">
                {movie.actors}
              </td>
              <td data-cell="year">
                {movie.year}
              </td>
              <td data-cell="poster">
                <img className="moviePoster" src={movie.poster}></img>
              </td>
            </tr>
          ))}
          </table>
          </div>) }
        
          </>
    )
}
