import { useState, useRef } from 'react';
import './App.css';
import { useReactToPrint } from 'react-to-print';

function FullUserList({ userLastList }) {
  return (
    <>
      <div style={{ width: '100%' }}>
        <table className="movieTable">
          <thead>
            <tr>
              <th>Movie Name</th>
              <th>IMDb</th>
              <th>Genre</th>
              <th>Plot</th>
              <th>Director</th>
              <th>Actors</th>
              <th>Year</th>
              <th>Poster</th>
            </tr>
          </thead>
          <tbody>
            {userLastList.map((movie) => (
              <tr key={movie.Title}>
                <td data-cell="title">{movie.title}</td>
                <td data-cell="imdb">{movie.imdbRating}</td>
                <td data-cell="genre">{movie.genre}</td>
                <td data-cell="plot">{movie.plot}</td>
                <td data-cell="director">{movie.director}</td>
                <td data-cell="title">{movie.actors}</td>
                <td data-cell="year">{movie.year}</td>
                <td data-cell="poster">
                  <img className="moviePoster" src={movie.poster} alt={movie.title} />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
}

export default function UserList({ userLastList, setUserLastList }) {
  const componentPDF = useRef();

  const [currentPage, setCurrentPage] = useState(1);
  const recordsPerPage = 5;
  const lastIndex = currentPage * recordsPerPage;
  const firstIndex = lastIndex - recordsPerPage;
  const records = userLastList.slice(firstIndex, lastIndex);
  const npage = Math.ceil(userLastList.length / recordsPerPage);
  const numbers = [...Array(npage + 1).keys()].slice(1);

  function changeCurrentPage(id) {
    setCurrentPage(id);
  }

  function prePage() {
    if (currentPage !== 1) {
      setCurrentPage(currentPage - 1);
    }
  }

  function nextPage() {
    if (currentPage !== npage) {
      setCurrentPage(currentPage + 1);
    }
  }

  function movieDelete(m) {
    const thisList = userLastList.filter((movie) => movie.id !== m.movie.id);
    setUserLastList(thisList);
  }

  const generatePDF = useReactToPrint({
    content: () => componentPDF.current,
    documentTitle: 'WatchList',
    onAfterPrint: () => alert('List saved in PDF'),
  });

  return (
    <>
      
        {/* Use FullUserList component for PDF generation */}
        <FullUserList userLastList={userLastList} ref={componentPDF} />
      
      <div className="tables">
        <button onClick={generatePDF}>PDF</button>
        <select className="selectClass withoutArrow">
          <option>Watch List</option>
        </select>
        {/* Display the paginated list on the page */}
        <table className="movieTable">
          <thead>
            <tr>
              <th>Movie Name</th>
              <th>IMDb</th>
              <th>Genre</th>
              <th>Plot</th>
              <th>Director</th>
              <th>Actors</th>
              <th>Year</th>
              <th>Poster</th>
              <th>Delete from Your List</th>
            </tr>
          </thead>
          <tbody>
            {records.map((movie) => (
              <tr key={movie.Title}>
                <td data-cell="title">{movie.title}</td>
                <td data-cell="imdb">{movie.imdbRating}</td>
                <td data-cell="genre">{movie.genre}</td>
                <td data-cell="plot">{movie.plot}</td>
                <td data-cell="director">{movie.director}</td>
                <td data-cell="title">{movie.actors}</td>
                <td data-cell="year">{movie.year}</td>
                <td data-cell="poster">
                  <img className="moviePoster" src={movie.poster} alt={movie.title} />
                </td>
                <td data-cell="delete">
                  <button
                    className="deleteButton"
                    onClick={() => movieDelete({ movie })}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <nav>
          <ul className="pagination">
            <li className="page-item">
              <button
                className="page-link"
                onClick={(e) => {
                  e.preventDefault();
                  prePage();
                }}
              >
                Prev
              </button>
            </li>
            {numbers.map((n, i) => (
              <li
                key={i}
                className={`page-item ${currentPage === n ? 'active' : ''}`}
              >
                <button
                  className="page-link"
                  onClick={(e) => {
                    e.preventDefault();
                    changeCurrentPage(n);
                  }}
                >
                  {n}
                </button>
              </li>
            ))}
            <li className="page-item">
              <button
                className="page-link"
                onClick={(e) => {
                  e.preventDefault();
                  nextPage();
                }}
              >
                Next
              </button>
            </li>
          </ul>
        </nav>
      </div>
    </>
  );
}
