import { useState } from 'react';
export default function Lalala(){
let [indexActive, setIsActive] = useState(0);
  return(
    <>
    <Panel onChange={() => {setIsActive(0)}}  isActive={indexActive === 0} title='Merhaba 1'>
      <p>This P is firts paragraph of my project</p>
    </Panel>
    <Panel onChange={() => {setIsActive(1)}}  title="Merhaba 2" isActive={indexActive === 1}>
      <p>This P is second paragraph of my project</p>
    </Panel>
    </>
  )
}
function Panel({children, title, onChange, isActive}){
  return(
    <>
    <div>
      {title}
      {isActive ? ({children}) : (
        <button onClick={onChange}>show</button>
      )}      
    </div>
    </>
  )
}
