import './App.css';

const initialTasks = [
  { id: 0, text: 'Visit Kafka Museum', done: true },
  { id: 1, text: 'Watch a puppet show', done: false },
  { id: 2, text: 'Lennon Wall pic', done: false },
];

export default function App() {
  return (
    <>
      <div>
        <AddTask />
        <ol>
          {initialTasks.map((initial) => (
            <li key={initial.id}>
              <input type="checkbox" value={initial.done} />
              {initial.text}
            </li>
          ))}
        </ol>
      </div>
    </>
  );
}

function AddTask(){
  return(
    <input type='text' value='add' />
  )
}
