import MoviesArray from "./data";
import './App.css';

function MovieList(){
  return(
    <>
    <div>
      <table className="movieTable">
      <tr>
        <th>Movie Name</th>
        <th>IMDb</th>
        <th>Genre</th>
        <th>Plot</th>
        <th>Director</th>
        <th>Actors</th>
        <th>Year</th>
        <th>Poster</th>
        <th>Add to Your List</th>
      </tr>
      {MoviesArray.map((movie) => (
        <tr>
          <td key={movie.Title}>
            {movie.Title}
          </td>
          <td key={movie.imdbRating}>
            {movie.imdbRating}
          </td>
          <td key={movie.Genre}>
            {movie.Genre}
          </td>
          <td key={movie.Plot}>
            {movie.Plot}
          </td>
          <td key={movie.Director}>
            {movie.Director}
          </td>
          <td key={movie.Actors}>
            {movie.Actors}
          </td>
          <td key={movie.Title + movie.Year}>
            {movie.Year}
          </td>
          <td  key={movie.Poster}>
            <img className="moviePoster" src={movie.Poster}></img>
          </td>
          <td>
            <button>Add To Your List</button>
          </td>
        </tr>
      ))}
      </table>
    </div>
    </>
  )
}
export default function App(){
  return(
    <>
    <MovieList />

    </>
  )
}
